import nodemailer from 'nodemailer';
import * as crypto from 'node:crypto';
import sgMail from '@sendgrid/mail';

// Email service configuration
export interface EmailConfig {
  host: string;
  port: number;
  secure: boolean;
  auth: {
    user: string;
    pass: string;
  };
}

// Email providers configurations
export const emailProviders = {
  gmail: {
    host: 'smtp.gmail.com',
    port: 587,
    secure: false, // TLS
  },
  outlook: {
    host: 'smtp-mail.outlook.com',
    port: 587,
    secure: false, // TLS
  },
  yahoo: {
    host: 'smtp.mail.yahoo.com',
    port: 587,
    secure: false, // TLS
  },
  sendgrid: {
    host: 'smtp.sendgrid.net',
    port: 587,
    secure: false, // TLS
  },
  mailgun: {
    host: 'smtp.mailgun.org',
    port: 587,
    secure: false, // TLS
  },
  // For development/testing
  ethereal: {
    host: 'smtp.ethereal.email',
    port: 587,
    secure: false,
  }
};

class EmailService {
  private transporter: nodemailer.Transporter | null = null;
  private fromEmail: string = '';
  private useSendGridAPI: boolean = false;

  private getBaseUrl(): string {
    // Always use production domain for deployed environments
    // Check for deployed environment indicators
    if (process.env.DEPLOYED_URL) {
      console.log('Email service using DEPLOYED_URL:', process.env.DEPLOYED_URL);
      return process.env.DEPLOYED_URL;
    }

    // Force production domain if we detect this is jigz.co deployment
    if (process.env.REPLIT_DEV_DOMAIN && process.env.REPLIT_DEV_DOMAIN.includes('jigz.co')) {
      console.log('Email service detected jigz.co domain, using production URL');
      return 'https://jigz.co';
    }

    if (process.env.NODE_ENV === 'production') {
      console.log('Email service using production environment');
      return 'https://jigz.co';
    } else {
      // For development
      const devUrl = process.env.REPLIT_DEV_DOMAIN 
        ? `https://${process.env.REPLIT_DEV_DOMAIN}`
        : `http://localhost:5000`;
      console.log('Email service detected BASE URL:', devUrl);
      return devUrl;
    }
  }

  async initialize() {
    // Check if SendGrid API key is available
    const sendGridApiKey = process.env.SENDGRID_API_KEY;
    if (sendGridApiKey) {
      sgMail.setApiKey(sendGridApiKey);
      this.useSendGridAPI = true;
      this.fromEmail = process.env.FROM_EMAIL || 'noreply@jigz.app';
      console.log('Email service initialized with SendGrid API');
      return true;
    }

    // Fallback to SMTP configuration
    const emailProvider = process.env.EMAIL_PROVIDER;
    const emailUser = process.env.EMAIL_USER;
    const emailPassword = process.env.EMAIL_PASSWORD;
    const fromEmail = process.env.FROM_EMAIL;

    if (!emailProvider || !emailUser || !emailPassword) {
      console.warn('Email service not configured. Email verification will be disabled.');
      return false;
    }

    let config: EmailConfig;

    if (emailProvider in emailProviders) {
      config = {
        ...emailProviders[emailProvider as keyof typeof emailProviders],
        auth: {
          user: emailUser,
          pass: emailPassword,
        },
      };
    } else if (emailProvider === 'custom') {
      // Custom SMTP configuration
      config = {
        host: process.env.SMTP_HOST || '',
        port: parseInt(process.env.SMTP_PORT || '587'),
        secure: process.env.SMTP_SECURE === 'true',
        auth: {
          user: emailUser,
          pass: emailPassword,
        },
      };
    } else {
      console.error(`Unsupported email provider: ${emailProvider}`);
      return false;
    }

    try {
      this.transporter = nodemailer.createTransport(config);
      this.fromEmail = fromEmail || emailUser;

      // Verify connection
      await this.transporter!.verify();
      console.log('Email service initialized successfully');
      return true;
    } catch (error) {
      console.error('Failed to initialize email service:', error);
      return false;
    }
  }

