import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Send, User, X, CheckCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { ConversationWithDetails, MessageWithSender } from "@shared/schema";
import { format } from "date-fns";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";

interface MessagingInterfaceProps {
  conversation: ConversationWithDetails;
  onClose?: () => void;
}

export function MessagingInterface({ conversation, onClose }: MessagingInterfaceProps) {
  const [messageText, setMessageText] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { user } = useAuth();

  // Fetch messages for this conversation
  const { data: messages = [], isLoading } = useQuery<MessageWithSender[]>({
    queryKey: ["/api/conversations", conversation.id, "messages"],
    refetchInterval: 5000, // Refetch every 5 seconds for real-time feel
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      const response = await apiRequest("POST", `/api/conversations/${conversation.id}/messages`, { content });
      return response.json();
    },
    onSuccess: () => {
      setMessageText("");
      // Invalidate messages to refresh
      queryClient.invalidateQueries({
        queryKey: ["/api/conversations", conversation.id, "messages"],
      });
      // Invalidate conversations list to update last message
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      // Invalidate unread count
      queryClient.invalidateQueries({ queryKey: ["/api/user/unread-messages"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Mark messages as read when conversation is opened
  const markAsReadMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("PATCH", `/api/conversations/${conversation.id}/mark-read`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/conversations"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/unread-messages"] });
    },
  });

  // Mark as read when conversation opens
  useEffect(() => {
    if (conversation.unreadCount > 0) {
      markAsReadMutation.mutate();
    }
  }, [conversation.id]);

  // Scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (messageText.trim() && !sendMessageMutation.isPending) {
      sendMessageMutation.mutate(messageText.trim());
    }
  };

  return (
    <>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="flex items-center gap-2 text-lg">
              <User className="h-5 w-5" />
              {conversation.otherUser?.name || 'Unknown User'}
              {conversation.otherUser?.isEmailVerified && (
                <CheckCircle className="h-4 w-4 text-green-600" />
              )}
            </CardTitle>
            <p className="text-sm text-gray-600 mt-1">
              Re: {conversation.service?.title || conversation.job?.title || 'Unknown Service'}
            </p>
          </div>
          {onClose && (
            <Button variant="ghost" size="sm" onClick={onClose}>
              <X className="h-4 w-4" />
            </Button>
          )}
        </div>
      </CardHeader>
      <Separator />

      <CardContent className="p-0 flex flex-col h-[calc(100vh-14rem)]">
        {/* Messages Area */}
        <ScrollArea className="flex-1 py-4 bg-gray-50">
          {isLoading ? (
            <div className="flex items-center justify-center h-32">
              <p className="text-gray-500">Loading messages...</p>
            </div>
          ) : messages.length === 0 ? (
            <div className="flex items-center justify-center h-32">
              <div className="text-center">
                <p className="text-gray-500 mb-2">No messages yet</p>
                <p className="text-sm text-gray-400 mb-3">Start the conversation!</p>
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 mt-2 max-w-sm mx-auto">
                  <p className="text-sm text-yellow-800 font-medium">
                    ‚ö†Ô∏è Please keep conversations professional and safe
                  </p>
                </div>
              </div>
            </div>
          ) : (
            <div className="space-y-3 px-2">
              {messages.map((message) => {
                const isCurrentUser = message.senderId === user?.id;

                return (
                  <div
                    key={message.id}
                    className={`flex ${isCurrentUser ? "justify-end" : "justify-start"}`}
                  >
                    <div
                      className={`max-w-[80%] p-3 rounded-2xl shadow-sm ${
                        isCurrentUser
                          ? "bg-blue-500 text-white rounded-br-md"
                          : "bg-white border border-gray-200 text-gray-900 rounded-bl-md"
                      }`}
                    >
                      <div className="flex items-center gap-2 mb-1">
                        <span className="text-xs font-medium">
                          {isCurrentUser ? "You" : message.sender.name}
                        </span>
                        <span className={`text-xs ${isCurrentUser ? "text-blue-100" : "text-gray-500"}`}>
                          {format(new Date(message.createdAt || 0), "MMM d, h:mm a")}
                        </span>
                      </div>
                      <p className="text-sm leading-relaxed">{message.content}</p>
                    </div>
                  </div>
                );
              })}
              <div ref={messagesEndRef} />
            </div>
          )}
        </ScrollArea>

        {/* Message Input */}
        <div className="border-t p-2">
          {messages.length <= 2 && (
            <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-2 mb-2">
              <p className="text-xs text-yellow-800 text-center">
                ‚ö†Ô∏è Please keep conversations professional and safe
              </p>
            </div>
          )}
          <form onSubmit={handleSendMessage} className="flex gap-2">
            <Input
              value={messageText}
              onChange={(e) => setMessageText(e.target.value)}
              placeholder="Type your message..."
              className="flex-1"
              disabled={sendMessageMutation.isPending}
            />
            <Button
              type="submit"
              disabled={!messageText.trim() || sendMessageMutation.isPending}
              size="sm"
            >
              <Send className="h-4 w-4" />
            </Button>
          </form>
        </div>
      </CardContent>
    </>
  );
}

client/src/components/post-job-modal.tsx.content:
import { useState } from "react";
import * as React from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { X, Upload, Coins } from "lucide-react";
// Removed Dialog imports - using custom modal instead
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { insertJobSchema, type JobWithApplications } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { z } from "zod";
import { CitySearch } from "@/components/city-search";
import { CurrencySearch } from "@/components/currency-search";
import { CategorySearch } from "@/components/category-search";
import { CoinWarning } from "@/components/coin-display";

const jobFormSchema = insertJobSchema.extend({
  images: z.any().optional(),
  specificArea: z.string().optional(),
  duration: z.string().optional(),
  customDuration: z.string().optional(),
  budgetType: z.string().default("fixed"),
  title: z.string().min(1, "Title is required").max(150, "Title must be 150 characters or less"),
  description: z.string().min(1, "Description is required").max(2500, "Description must be 2500 characters or less"),
});

type JobFormData = z.infer<typeof jobFormSchema>;

interface PostJobModalProps {
  isOpen: boolean;
  onClose: () => void;
  editingJob?: JobWithApplications | null;
  isEditing?: boolean;
}

// Categories are now handled by CategorySearch component

export default function PostJobModal({ isOpen, onClose, editingJob, isEditing = false }: PostJobModalProps) {
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [coinError, setCoinError] = useState<{ coinsNeeded: number; coinsAvailable: number } | null>(null);
  const [isSpecificAreaEditing, setIsSpecificAreaEditing] = useState(false);
  const [showCustomDuration, setShowCustomDuration] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Debug logging


  const form = useForm<JobFormData>({
    resolver: zodResolver(jobFormSchema),
    defaultValues: {
      title: "",
      description: "",
      category: "",
      location: "",
      maxBudget: undefined,
      budgetType: "fixed",
      currency: "USD",
      experienceLevel: "any",
      status: "open",
      images: [],
      specificArea: "",
      duration: "",
      customDuration: "",
      freelancersNeeded: 1,
    },
  });

  // Reset form when modal opens/closes or when editing job changes
  React.useEffect(() => {
    if (isOpen && isEditing && editingJob) {
      form.reset({
        title: editingJob.title || "",
        description: editingJob.description || "",
        category: editingJob.category || "",
        location: editingJob.location || "",
        specificArea: editingJob.specificArea || "",
        maxBudget: editingJob.maxBudget || undefined,
        minBudget: editingJob.minBudget || undefined,
        budgetType: editingJob.budgetType || "fixed",
        currency: editingJob.currency || "USD",
        experienceLevel: editingJob.experienceLevel || "any",
        duration: editingJob.duration || "",
        customDuration: editingJob.customDuration || "",
        status: editingJob.status || "open",
        images: [],
        freelancersNeeded: editingJob.freelancersNeeded || 1,
      });
      setShowCustomDuration(editingJob.duration === "custom");
    } else if (isOpen && !isEditing) {
      form.reset({
        title: "",
        description: "",
        category: "",
        location: "",
        specificArea: "",
        maxBudget: undefined,
        budgetType: "fixed",
        currency: "USD",
        experienceLevel: "any",
        status: "open",
        images: [],
        duration: "",
        customDuration: "",
        freelancersNeeded: 1,
      });
      setShowCustomDuration(false);
    }
    if (!isOpen) {
      setSelectedFiles([]);
    }
  }, [isOpen, isEditing, editingJob, form]);

  const createJobMutation = useMutation({
    mutationFn: async (data: JobFormData) => {
      const formData = new FormData();

      // Append job data
      Object.entries(data).forEach(([key, value]) => {
        if (key !== "images" && value !== undefined && value !== null) {
          formData.append(key, value.toString());
        }
      });

      // Append images
      selectedFiles.forEach((file) => {
        formData.append("images", file);
      });

      const method = isEditing ? "PUT" : "POST";
      const url = isEditing ? `/api/jobs/${editingJob?.id}` : "/api/jobs";

      const res = await fetch(url, {
        method,
        body: formData,
      });

      if (!res.ok) {
        throw new Error(isEditing ? "Failed to update job" : "Failed to create job");
      }

      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/coins"] });
      setCoinError(null);
      toast({
        title: "Success",
        description: isEditing 
          ? "Job updated successfully! Your updated job is now under review by our team and will be approved within 30 minutes. 1 coin has been deducted." 
          : "Job posted successfully! Your posting is now under review by our team. Most jobs are approved within 30 minutes and we're working tirelessly to get it live! 3 coins have been deducted.",
      });
      onClose();
      form.reset();
      setSelectedFiles([]);
    },
    onError: (error: any) => {
      console.error("Error posting job:", error);

      // Check if it's a coin-related error
      if (error.coinsNeeded && error.coinsAvailable !== undefined) {
        setCoinError({
          coinsNeeded: error.coinsNeeded,
          coinsAvailable: error.coinsAvailable
        });
        return;
      }

      toast({
        title: "Error",
        description: error.message || "Failed to post job. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);
    setSelectedFiles(prev => [...prev, ...files].slice(0, 5)); // Max 5 images
  };

  const removeFile = (index: number) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const onSubmit = (data: JobFormData) => {
    createJobMutation.mutate(data);
  };



  // Create a simple modal without Dialog component complications
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      {/* Backdrop */}
      <div 
        className="absolute inset-0 bg-black/50" 
        onClick={onClose}
      />

      {/* Modal Content */}
      <div 
        className="relative w-full h-full sm:w-auto sm:h-auto sm:max-w-4xl sm:max-h-[90vh] bg-white sm:rounded-lg overflow-hidden flex flex-col sm:m-4"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Mobile Header with Close Button */}
        <div className="sticky top-0 z-10 bg-white border-b border-gray-200 p-4 flex items-center justify-between sm:hidden">
          <div>
            <h2 className="text-lg font-semibold">{isEditing ? "Edit Job" : "Post Job"}</h2>
            <p className="text-sm text-gray-600">{isEditing ? "Editing costs 1 coin" : "Posting costs 3 coins"}</p>
          </div>
          <button
            type="button"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();

              onClose();
            }}
            className="p-2 hover:bg-gray-100 rounded-full"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Desktop Header */}
        <div className="pb-4 hidden sm:block p-6 border-b border-gray-200 relative">
          <button
            onClick={onClose}
            className="absolute top-4 right-4 p-2 hover:bg-gray-100 rounded-full transition-colors"
            type="button"
          >
            <X className="h-5 w-5 text-gray-500" />
          </button>
          <h2 className="text-xl font-semibold pr-12">{isEditing ? "Edit Job" : "Post a New Job"}</h2>
          <p className="text-gray-600 mt-1">
            {isEditing ? "Update the details below to modify your job posting. Editing costs 1 coin and requires admin re-approval." : "Fill out the details below to post your job and start receiving applications. Posting costs 3 coins."}
          </p>
        </div>

        {/* Content Container */}
        <div className="flex-1 overflow-y-auto p-4 sm:p-6">

          {coinError && (
            <CoinWarning 
              coinsNeeded={coinError.coinsNeeded} 
              coinsAvailable={coinError.coinsAvailable} 
            />
          )}

          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4 sm:space-y-6 w-full pb-20 sm:pb-0 sm:grid sm:gap-6 sm:grid-cols-1">
          {/* Job Title */}
          <div>
            <div className="flex justify-between items-center">
              <Label htmlFor="title">Job Title *</Label>
              <span className="text-xs text-gray-500">
                {form.watch("title")?.length || 0}/150
              </span>
            </div>
            <Input
              id="title"
              placeholder="e.g., Help hang picture frames"
              {...form.register("title")}
              className="mt-1 text-sm sm:text-base w-full"
              maxLength={150}
            />
            {form.formState.errors.title && (
              <p className="text-sm text-red-600 mt-1">{form.formState.errors.title.message}</p>
            )}
          </div>

          {/* Description */}
          <div>
            <div className="flex justify-between items-center">
              <Label htmlFor="description">Description *</Label>
              <span className="text-xs text-gray-500">
                {form.watch("description")?.length || 0}/2500
              </span>
            </div>
            <Textarea
              id="description"
              rows={3}
              placeholder="Describe what you need help with..."
              {...form.register("description")}
              className="mt-1 resize-none text-sm sm:text-base w-full"
              maxLength={2500}
            />
            {form.formState.errors.description && (
              <p className="text-sm text-red-600 mt-1">{form.formState.errors.description.message}</p>
            )}
          </div>

          {/* Job Duration */}
          <div>
            <Label>How long will this job take?</Label>
            <Select 
              value={form.watch("duration") || ""}
              onValueChange={(value) => {
                form.setValue("duration", value);
                setShowCustomDuration(value === "custom");
                if (value !== "custom") {
                  form.setValue("customDuration", "");
                }
              }}
            >
              <SelectTrigger className="mt-1 w-full">
                <SelectValue placeholder="Select expected duration" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="a couple hours">A couple hours</SelectItem>
                <SelectItem value="a day">A day</SelectItem>
                <SelectItem value="a couple of days">A couple of days</SelectItem>
                <SelectItem value="a week">A week</SelectItem>
                <SelectItem value="less than a month">Less than a month</SelectItem>
                <SelectItem value="1-3 months">1-3 months</SelectItem>
                <SelectItem value="3+ months">3+ months</SelectItem>
                <SelectItem value="custom">Custom length</SelectItem>
              </SelectContent>
            </Select>

            {/* Custom Duration Input */}
            {showCustomDuration && (
              <div className="mt-2">
                <Input
                  placeholder="Describe the expected duration..."
                  {...form.register("customDuration")}
                  className="mt-1"
                />
              </div>
            )}
          </div>

          {/* Category and Location - Desktop Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 sm:gap-6">
            {/* Category */}
            <div>
              <Label>Category *</Label>
              <div className="mt-1 w-full">
                <CategorySearch
                  value={form.watch("category") || ""}
                  onValueChange={(value) => form.setValue("category", value)}
                  placeholder="Search and select category..."
                />
              </div>
              {form.formState.errors.category && (
                <p className="text-sm text-red-600 mt-1">{form.formState.errors.category.message}</p>
              )}
            </div>

            {/* Location */}
            <div>
              <Label>City *</Label>
              <div className="mt-1 w-full">
                <CitySearch
                  value={form.watch("location") || ""}
                  onValueChange={(value) => form.setValue("location", value)}
                  placeholder="Search and select your city..."
                />
              </div>
              {form.formState.errors.location && (
                <p className="text-sm text-red-600 mt-1">{form.formState.errors.location.message}</p>
              )}
            </div>
          </div>

          {/* Specific Area */}
          <div>
            <Label htmlFor="specificArea">Specific Area (Optional)</Label>
            {isSpecificAreaEditing ? (
              <div className="flex flex-col sm:flex-row gap-2 mt-1">
                <Input
                  id="specificArea"
                  placeholder="e.g., Downtown, Upper East Side, Financial District"
                  {...form.register("specificArea")}
                  className="flex-1"
                  autoFocus
                  onBlur={() => setIsSpecificAreaEditing(false)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      setIsSpecificAreaEditing(false);
                    }
                    if (e.key === 'Escape') {
                      setIsSpecificAreaEditing(false);
                    }
                  }}
                />
                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => setIsSpecificAreaEditing(false)}
                  className="w-full sm:w-auto"
                >
                  Done
                </Button>
              </div>
            ) : (
              <Button
                type="button"
                variant="outline"
                className="w-full justify-start text-left h-auto p-3 mt-1"
                onClick={() => setIsSpecificAreaEditing(true)}
              >
                <div className="flex flex-col items-start text-left">
                  <span className="text-sm font-medium">
                    {form.watch("specificArea") ? "Edit Specific Area" : "Add Specific Area"}
                  </span>
                  <span className="text-xs text-gray-500 mt-1 break-words">
                    {form.watch("specificArea") 
                      ? `Area: ${form.watch("specificArea")}` 
                      : "Click to add neighborhood, district, or specific location details"}
                  </span>
                </div>
              </Button>
            )}
          </div>

          {/* Budget Type */}
          <div>
            <Label>Budget Type</Label>
            <div className="flex flex-col sm:flex-row gap-2 mt-1">
              <Button
                type="button"
                variant={form.watch("budgetType") === "fixed" ? "default" : "outline"}
                className={`flex-1 ${form.watch("budgetType") === "fixed" ? "bg-blue-600 hover:bg-blue-700" : ""}`}
                onClick={() => form.setValue("budgetType", "fixed")}
              >
                Fixed Price
              </Button>
              <Button
                type="button"
                variant={form.watch("budgetType") === "hourly" ? "default" : "outline"}
                className={`flex-1 ${form.watch("budgetType") === "hourly" ? "bg-blue-600 hover:bg-blue-700" : ""}`}
                onClick={() => form.setValue("budgetType", "hourly")}
              >
                Hourly Rate
              </Button>
            </div>
          </div>

          {/* Budget and Currency */}
          <div>
            <Label htmlFor="maxBudget">
              {form.watch("budgetType") === "hourly" ? "Hourly Rate" : "Budget"}
            </Label>
            <div className="flex gap-0 mt-1 w-full">
              <div className="w-auto min-w-[120px]">
                <CurrencySearch
                  value={form.watch("currency") || "USD"}
                  onValueChange={(value) => form.setValue("currency", value || "USD")}
                />
              </div>
              <Input
                id="maxBudget"
                type="number"
                placeholder={form.watch("budgetType") === "hourly" ? "25" : "50"}
                {...form.register("maxBudget", { valueAsNumber: true })}
                className="flex-1 rounded-l-none border-l-0"
              />
            </div>
            {form.watch("budgetType") === "hourly" && (
              <p className="text-xs text-gray-500 mt-1">Enter your hourly rate</p>
            )}
          </div>

          {/* Experience Level and Freelancers Needed - Desktop Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 sm:gap-6">
            {/* Experience Level */}
            <div>
              <Label>Experience Level</Label>
              <Select onValueChange={(value) => form.setValue("experienceLevel", value)} defaultValue="any">
                <SelectTrigger className="mt-1 w-full">
                  <SelectValue placeholder="Select experience level" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="any">Any</SelectItem>
                  <SelectItem value="beginner">Beginner</SelectItem>
                  <SelectItem value="intermediate">Intermediate</SelectItem>
                  <SelectItem value="expert">Expert</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Number of Freelancers Needed */}
            <div>
              <Label htmlFor="freelancersNeeded">Number of Freelancers Needed</Label>
              <Input
                id="freelancersNeeded"
                type="number"
                min="1"
                max="50"
                placeholder="1"
                {...form.register("freelancersNeeded", { valueAsNumber: true })}
                className="mt-1 w-full"
              />
              <p className="text-xs text-gray-500 mt-1">How many freelancers do you need for this job? (Default: 1)</p>
              {form.formState.errors.freelancersNeeded && (
                <p className="text-sm text-red-600 mt-1">{form.formState.errors.freelancersNeeded.message}</p>
              )}
            </div>
          </div>

          {/* Image Upload */}
          <div>
            <Label>Photos (Optional)</Label>
            <div className="mt-1">
              <label className="border-2 border-dashed border-gray-300 rounded-md p-4 sm:p-6 text-center hover:border-blue-600 transition-colors cursor-pointer block">
                <Upload className="h-6 w-6 sm:h-8 sm:w-8 text-gray-400 mx-auto mb-2" />
                <p className="text-xs sm:text-sm text-gray-600">Click to upload photos or drag and drop</p>
                <p className="text-xs text-gray-500 mt-1">PNG, JPG up to 10MB each (max 5 photos)</p>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleFileSelect}
                  className="hidden"
                />
              </label>
            </div>

            {/* Preview selected files */}
            {selectedFiles.length > 0 && (
              <div className="mt-4 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2 sm:gap-4">
                {selectedFiles.map((file, index) => (
                  <div key={index} className="relative">
                    <img
                      src={URL.createObjectURL(file)}
                      alt={`Preview ${index + 1}`}
                      className="w-full h-16 sm:h-20 object-cover rounded border"
                    />
                    <button
                      type="button"
                      onClick={() => removeFile(index)}
                      className="absolute -top-1 -right-1 sm:-top-2 sm:-right-2 bg-red-500 text-white rounded-full w-5 h-5 sm:w-6 sm:h-6 flex items-center justify-center text-xs hover:bg-red-600"
                    >
                      <X className="h-3 w-3 sm:h-4 sm:w-4" />
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Form Actions */}
          <div className="flex flex-col sm:flex-row gap-3 sm:gap-4 pt-4 border-t border-gray-200">
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              className="w-full flex-1 order-2 sm:order-1"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={createJobMutation.isPending}
              className="w-full flex-1 bg-blue-600 hover:bg-blue-700 order-1 sm:order-2"
            >
              <span className="flex items-center justify-center gap-1">
                {createJobMutation.isPending ? (
                  isEditing ? "Updating..." : "Posting..."
                ) : (
                  <>
                    <span>
                      {isEditing ? "Update Job" : "Post Job"}
                    </span>
                    <span className="ml-1">
                      ({isEditing ? "1" : "3"}<Coins className="h-4 w-4 inline text-yellow-500 ml-0.5" />)
                    </span>
                  </>
                )}
              </span>
            </Button>
          </div>
          </form>
        </div>
      </div>
    </div>
  );
}

client/src/components/rating-display.tsx.content:
import { Star } from "lucide-react";
import { cn } from "@/lib/utils";

interface RatingDisplayProps {
  rating: number;
  totalReviews?: number;
  size?: "sm" | "md" | "lg";
  showNumber?: boolean;
  className?: string;
}

export function RatingDisplay({ 
  rating, 
  totalReviews, 
  size = "md", 
  showNumber = true,
  className 
}: RatingDisplayProps) {
  const sizeClasses = {
    sm: "h-3 w-3",
    md: "h-4 w-4",
    lg: "h-5 w-5"
  };

  const textSizeClasses = {
    sm: "text-xs",
    md: "text-sm",
    lg: "text-base"
  };

  return (
    <div className={cn("flex items-center gap-1", className)}>
      <div className="flex items-center">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={cn(
              sizeClasses[size],
              star <= rating
                ? "fill-yellow-400 text-yellow-400"
                : "fill-gray-200 text-gray-200"
            )}
          />
        ))}
      </div>
      {showNumber && (
        <span className={cn("text-gray-600 font-medium", textSizeClasses[size])}>
          {rating.toFixed(1)}
          {totalReviews !== undefined && (
            <span className="text-gray-400 font-normal"> ({totalReviews})</span>
          )}
        </span>
      )}
    </div>
  );
}

client/src/components/report-job-modal.t.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useToast } from "@/hooks/use-toast";
import { Flag, AlertTriangle, Mail, EyeOff, Users, Shield, MoreHorizontal } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface ReportJobModalProps {
  isOpen: boolean;
  onClose: () => void;
  jobId: string;
  jobTitle: string;
}

const reportCategories = [
  {
    value: "spam",
    label: "Spam or Fake Job",
    description: "This job appears to be spam, fake, or misleading",
    icon: Mail
  },
  {
    value: "inappropriate",
    label: "Inappropriate Content",
    description: "Contains offensive, adult, or inappropriate content",
    icon: EyeOff
  },
  {
    value: "discriminatory",
    label: "Discriminatory",
    description: "Contains discriminatory language or requirements",
    icon: Users
  },
  {
    value: "unsafe",
    label: "Unsafe or Illegal",
    description: "Promotes unsafe practices or illegal activities",
    icon: Shield
  },
  {
    value: "fake",
    label: "Fraudulent",
    description: "This appears to be a scam or fraudulent job posting",
    icon: AlertTriangle
  },
  {
    value: "other",
    label: "Other",
    description: "Something else that violates community guidelines",
    icon: MoreHorizontal
  }
];

export function ReportJobModal({ isOpen, onClose, jobId, jobTitle }: ReportJobModalProps) {
  const [category, setCategory] = useState<string>("");
  const [reason, setReason] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!category) {
      toast({
        title: "Category Required",
        description: "Please select a report category.",
        variant: "destructive"
      });
      return;
    }

    if (reason.trim().length < 10) {
      toast({
        title: "Reason Required",
        description: "Please provide a detailed reason (minimum 10 characters).",
        variant: "destructive"
      });
      return;
    }

    setIsSubmitting(true);

    try {
      await apiRequest("POST", `/api/jobs/${jobId}/reports`, {
        category,
        reason: reason.trim()
      });

      toast({
        title: "Report Submitted",
        description: "Thank you for your report. Our team will review it promptly."
      });

      // Invalidate job-related queries
      queryClient.invalidateQueries({ queryKey: ["/api/search/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/jobs", jobId, "reports", "check"] });

      onClose();
      setCategory("");
      setReason("");
    } catch (error: any) {
      toast({
        title: "Report Failed",
        description: error.message || "Failed to submit report. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleClose = () => {
    if (!isSubmitting) {
      onClose();
      setCategory("");
      setReason("");
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Flag className="h-5 w-5 text-red-500" />
            Report Job
          </DialogTitle>
          <DialogDescription>
            Report "{jobTitle}" for violating community guidelines. Your report will be reviewed by our moderation team.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <Label className="text-sm font-medium">Why are you reporting this job?</Label>
            <RadioGroup value={category} onValueChange={setCategory} className="mt-3">
              {reportCategories.map((cat) => {
                const IconComponent = cat.icon;
                return (
                  <div key={cat.value} className="flex items-start space-x-3 p-3 rounded-lg border hover:bg-muted/50 transition-colors">
                    <RadioGroupItem value={cat.value} id={cat.value} className="mt-0.5" />
                    <div className="flex-1 space-y-1">
                      <div className="flex items-center gap-2">
                        <IconComponent className="h-4 w-4 text-muted-foreground" />
                        <Label htmlFor={cat.value} className="font-medium cursor-pointer">
                          {cat.label}
                        </Label>
                      </div>
                      <p className="text-sm text-muted-foreground">{cat.description}</p>
                    </div>
                  </div>
                );
              })}
            </RadioGroup>
          </div>

          <div>
            <Label htmlFor="reason" className="text-sm font-medium">
              Additional Details
            </Label>
            <Textarea
              id="reason"
              placeholder="Please provide specific details about why you're reporting this job (minimum 10 characters)..."
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              className="mt-2 min-h-[100px]"
              maxLength={500}
            />
            <p className="text-xs text-muted-foreground mt-1">
              {reason.length}/500 characters (minimum 10 required)
            </p>
          </div>

          <div className="flex justify-end gap-3">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              disabled={isSubmitting}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              variant="destructive"
              disabled={isSubmitting || !category || reason.trim().length < 10}
              className="flex items-center gap-2"
            >
              {isSubmitting ? (
                <>
                  <div className="h-4 w-4 animate-spin rounded-full border-2 border-background border-t-transparent" />
                  Submitting...
                </>
              ) : (
                <>
                  <Flag className="h-4 w-4" />
                  Submit Report
                </>
              )}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/review-modal.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { Star, X, Award } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { insertReviewSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { z } from "zod";
import { cn } from "@/lib/utils";
import { SkillEndorsementModal } from "@/components/skill-endorsement-modal";

const reviewFormSchema = insertReviewSchema;

type ReviewFormData = z.infer<typeof reviewFormSchema>;

interface ReviewModalProps {
  isOpen: boolean;
  onClose: () => void;
  jobId: string;
  revieweeId: string;
  revieweeName: string;
  reviewType: "client_to_worker" | "worker_to_client";
  jobTitle?: string;
}

export default function ReviewModal({ 
  isOpen, 
  onClose, 
  jobId, 
  revieweeId, 
  revieweeName,
  reviewType,
  jobTitle 
}: ReviewModalProps) {
  const [hoveredRating, setHoveredRating] = useState(0);
  const [hoveredQualityRating, setHoveredQualityRating] = useState(0);
  const [hoveredCommunicationRating, setHoveredCommunicationRating] = useState(0);
  const [hoveredTimelinessRating, setHoveredTimelinessRating] = useState(0);
  const [skillEndorsementModalOpen, setSkillEndorsementModalOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user } = useAuth();

  // Check if user has already endorsed this person for this job
  const { data: endorsementStatus } = useQuery<{ hasEndorsed: boolean }>({
    queryKey: [`/api/jobs/${jobId}/has-endorsed/${revieweeId}`],
    enabled: isOpen && reviewType === "client_to_worker" && !!user?.id && !!jobId && !!revieweeId,
    retry: false,
  });

  const form = useForm<ReviewFormData>({
    resolver: zodResolver(reviewFormSchema),
    defaultValues: {
      jobId,
      reviewerId: user?.id || "",
      revieweeId,
      rating: reviewType === "worker_to_client" ? 0 : 1, // Auto-set to 1 for client-to-worker (will be calculated)
      comment: "",
      reviewType,
      qualityOfWorkRating: reviewType === "client_to_worker" ? 0 : undefined,
      communicationRating: reviewType === "client_to_worker" ? 0 : undefined,
      timelinessRating: reviewType === "client_to_worker" ? 0 : undefined,
    },
  });

  const reviewMutation = useMutation({
    mutationFn: (data: ReviewFormData) => {
      // Use different endpoint for worker-to-client reviews
      if (data.reviewType === "worker_to_client") {
        // Transform data for the rate-client endpoint - use props directly since form data may be empty
        const clientRatingData = {
          jobId: jobId, // Use the prop instead of form data
          clientId: revieweeId, // Use the prop instead of form data
          rating: data.rating,
          comment: data.comment,
        };

        return apiRequest("POST", "/api/reviews/rate-client", clientRatingData);
      } else {
        return apiRequest("POST", "/api/reviews", data);
      }
    },
    onSuccess: () => {
      toast({
        title: "Review submitted",
        description: reviewType === "worker_to_client" 
          ? "Thank you for rating the client!" 
          : "Thank you for your feedback!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/user/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/applications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/reviews"] });
      queryClient.invalidateQueries({ queryKey: ["/api/client-rating-eligibility"] });
      queryClient.invalidateQueries({ queryKey: ["/api/reviews/rating-eligibility"] });
      queryClient.invalidateQueries({ queryKey: ["/api/freelancer-review-status"] });
      onClose();
      form.reset();
    },
    onError: (error: any) => {
      console.error("Review submission error:", error);
      toast({
        title: "Error",
        description: error?.message || "Failed to submit review. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ReviewFormData) => {
    // For worker-to-client reviews, require overall rating
    if (data.reviewType === "worker_to_client" && data.rating === 0) {
      toast({
        title: "Rating required",
        description: "Please select a rating before submitting.",
        variant: "destructive",
      });
      return;
    }

    // For client-to-worker reviews, require all detailed ratings and set overall rating to average
    if (data.reviewType === "client_to_worker") {
      if (!data.qualityOfWorkRating || !data.communicationRating || !data.timelinessRating) {
        toast({
          title: "Detailed ratings required",
          description: "Please rate all three aspects: Quality of Work, Communication & Professionalism, and Timeliness & Reliability.",
          variant: "destructive",
        });
        return;
      }
      // Calculate average rating from detailed ratings
      const averageRating = Math.round((data.qualityOfWorkRating + data.communicationRating + data.timelinessRating) / 3);
      data.rating = averageRating;
    }

    reviewMutation.mutate(data);
  };

  const rating = form.watch("rating");

  return (
    <>
      <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>
            Rate {reviewType === "client_to_worker" ? "Service Provider" : "Client"}
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
          {/* Only show overall rating for worker-to-client reviews */}
          {reviewType === "worker_to_client" && (
            <div>
              <Label className="text-sm font-medium">
                How was your experience with {revieweeName}?
              </Label>
              <div className="flex items-center gap-1 mt-2">
                {[1, 2, 3, 4, 5].map((star) => (
                  <button
                    key={star}
                    type="button"
                    className="p-1"
                    onMouseEnter={() => setHoveredRating(star)}
                    onMouseLeave={() => setHoveredRating(0)}
                    onClick={() => form.setValue("rating", star)}
                  >
                    <Star
                      className={cn(
                        "h-8 w-8 transition-colors",
                        star <= (hoveredRating || rating)
                          ? "fill-yellow-400 text-yellow-400"
                          : "fill-gray-200 text-gray-200 hover:fill-yellow-200 hover:text-yellow-200"
                      )}
                    />
                  </button>
                ))}
              </div>
              {rating > 0 && (
                <p className="text-sm text-gray-600 mt-1">
                  {rating === 1 && "Poor"}
                  {rating === 2 && "Fair"}
                  {rating === 3 && "Good"}
                  {rating === 4 && "Very Good"}
                  {rating === 5 && "Excellent"}
                </p>
              )}
            </div>
          )}

          {/* Detailed ratings for client-to-freelancer reviews */}
          {reviewType === "client_to_worker" && (
            <div className="space-y-4">
              <h3 className="font-medium text-sm">Rate {revieweeName} on the following aspects:</h3>

              {/* Quality of Work Rating */}
              <div>
                <Label className="text-sm">Quality of Work</Label>
                <div className="flex items-center gap-1 mt-1">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <button
                      key={star}
                      type="button"
                      className="p-1"
                      onMouseEnter={() => setHoveredQualityRating(star)}
                      onMouseLeave={() => setHoveredQualityRating(0)}
                      onClick={() => form.setValue("qualityOfWorkRating", star)}
                    >
                      <Star
                        className={cn(
                          "h-6 w-6 transition-colors",
                          star <= (hoveredQualityRating || form.watch("qualityOfWorkRating") || 0)
                            ? "fill-yellow-400 text-yellow-400"
                            : "fill-gray-200 text-gray-200 hover:fill-yellow-200 hover:text-yellow-200"
                        )}
                      />
                    </button>
                  ))}
                </div>
              </div>

              {/* Communication & Professionalism Rating */}
              <div>
                <Label className="text-sm">Communication & Professionalism</Label>
                <div className="flex items-center gap-1 mt-1">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <button
                      key={star}
                      type="button"
                      className="p-1"
                      onMouseEnter={() => setHoveredCommunicationRating(star)}
                      onMouseLeave={() => setHoveredCommunicationRating(0)}
                      onClick={() => form.setValue("communicationRating", star)}
                    >
                      <Star
                        className={cn(
                          "h-6 w-6 transition-colors",
                          star <= (hoveredCommunicationRating || form.watch("communicationRating") || 0)
                            ? "fill-yellow-400 text-yellow-400"
                            : "fill-gray-200 text-gray-200 hover:fill-yellow-200 hover:text-yellow-200"
                        )}
                      />
                    </button>
                  ))}
                </div>
              </div>

              {/* Timeliness & Reliability Rating */}
              <div>
                <Label className="text-sm">Timeliness & Reliability</Label>
                <div className="flex items-center gap-1 mt-1">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <button
                      key={star}
                      type="button"
                      className="p-1"
                      onMouseEnter={() => setHoveredTimelinessRating(star)}
                      onMouseLeave={() => setHoveredTimelinessRating(0)}
                      onClick={() => form.setValue("timelinessRating", star)}
                    >
                      <Star
                        className={cn(
                          "h-6 w-6 transition-colors",
                          star <= (hoveredTimelinessRating || form.watch("timelinessRating") || 0)
                            ? "fill-yellow-400 text-yellow-400"
                            : "fill-gray-200 text-gray-200 hover:fill-yellow-200 hover:text-yellow-200"
                        )}
                      />
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}

          <div>
            <Label htmlFor="comment" className="text-sm font-medium">
              Leave a comment (optional)
            </Label>
            <Textarea
              id="comment"
              placeholder="Share your experience..."
              className="mt-2"
              rows={4}
              {...form.register("comment")}
            />
          </div>

          <div className="flex gap-3">
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              className="flex-1"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={reviewMutation.isPending || 
                (reviewType === "worker_to_client" && rating === 0) ||
                (reviewType === "client_to_worker" && 
                 (!form.watch("qualityOfWorkRating") || !form.watch("communicationRating") || !form.watch("timelinessRating")))}
              className="flex-1 bg-blue-600 hover:bg-blue-700"
            >
              {reviewMutation.isPending ? "Submitting..." : "Submit Review"}
            </Button>
          </div>

          {/* Endorse Skills Button - only for client-to-worker reviews */}
          {reviewType === "client_to_worker" && (
            <div className="pt-4 border-t">
              <p className="text-sm text-gray-600 mb-3">
                Want to highlight {revieweeName}'s specific skills? Endorse them for 5 coins.
              </p>
              {endorsementStatus?.hasEndorsed ? (
                <Button
                  type="button"
                  variant="outline"
                  disabled
                  className="w-full bg-gray-50 text-gray-500 border-gray-200 cursor-not-allowed"
                >
                  <Award className="h-4 w-4 mr-2" />
                  Already Endorsed
                </Button>
              ) : (
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setSkillEndorsementModalOpen(true)}
                  className="w-full bg-blue-50 text-blue-700 border-blue-200 hover:bg-blue-100"
                >
                  <Award className="h-4 w-4 mr-2" />
                  Endorse Skills
                </Button>
              )}
            </div>
          )}
        </form>
      </DialogContent>
    </Dialog>

      <SkillEndorsementModal
        isOpen={skillEndorsementModalOpen}
        onClose={() => setSkillEndorsementModalOpen(false)}
        endorseeId={revieweeId}
        endorseeName={revieweeName}
        jobId={jobId}
        jobTitle={jobTitle || ""}
      />
    </>
  );
}

