import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Coins, TrendingUp, Info } from "lucide-react";
import type { ApplicationWithJob } from "@shared/schema";
import { CoinWarning } from "@/components/coin-display";

interface BidManagementProps {
  application: ApplicationWithJob;
}

export function BidManagement({ application }: BidManagementProps) {
  const [additionalCoins, setAdditionalCoins] = useState<number>(1);
  const [coinError, setCoinError] = useState<{ coinsNeeded: number; coinsAvailable: number } | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const increaseBidMutation = useMutation({
    mutationFn: async (data: { additionalCoins: number }) => {
      const res = await apiRequest("POST", `/api/applications/${application.id}/bid`, data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user/applications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/coins"] });
      queryClient.invalidateQueries({ queryKey: ["/api/jobs", application.jobId, "application-rankings"] });
      setCoinError(null);
      toast({
        title: "Bid Increased",
        description: `Added ${additionalCoins} coins to your bid. Your application now has higher priority!`,
      });
      setAdditionalCoins(1);
    },
    onError: (error: any) => {
      console.error("Error increasing bid:", error);

      // Check if it's a coin-related error
      if (error.coinsNeeded && error.coinsAvailable !== undefined) {
        setCoinError({
          coinsNeeded: error.coinsNeeded,
          coinsAvailable: error.coinsAvailable
        });
        return;
      }

      toast({
        title: "Error",
        description: error.message || "Failed to increase bid. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleIncreaseBid = () => {
    if (additionalCoins < 1) {
      toast({
        title: "Invalid Amount",
        description: "You must bid at least 1 additional coin.",
        variant: "destructive",
      });
      return;
    }
    increaseBidMutation.mutate({ additionalCoins });
  };

  const currentBid = application.coinsBid || 0;
  const isApplicationPending = application.status === 'pending';

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-sm">
          <TrendingUp className="h-4 w-4" />
          Priority Bidding
          {currentBid > 0 && (
            <Badge variant="secondary" className="ml-auto">
              <Coins className="h-3 w-3 mr-1" />
              {currentBid} coins
            </Badge>
          )}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {coinError && (
          <CoinWarning 
            coinsNeeded={coinError.coinsNeeded} 
            coinsAvailable={coinError.coinsAvailable} 
          />
        )}

        <div className="flex items-start gap-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
          <Info className="h-4 w-4 text-blue-600 mt-0.5 flex-shrink-0" />
          <div className="text-xs text-blue-700">
            <p className="font-medium mb-1">How Priority Bidding Works:</p>
            <ul className="space-y-0.5 list-disc list-inside">
              <li>Higher bids rank your application at the top</li>
              <li>Clients see top bidders first</li>
              <li>Coins are spent immediately when bid is placed</li>
            </ul>
          </div>
        </div>

        {currentBid === 0 ? (
          <div className="text-center py-2">
            <p className="text-sm text-gray-600 mb-2">
              No priority bid placed yet
            </p>
            <Badge variant="outline" className="text-xs">
              Standard ranking
            </Badge>
          </div>
        ) : (
          <div className="text-center py-2">
            <div className="flex items-center justify-center gap-2 text-sm font-medium text-green-700">
              <Coins className="h-4 w-4" />
              Current bid: {currentBid} coins
            </div>
            <Badge variant="default" className="text-xs mt-1">
              Priority ranking active ‚ö°
            </Badge>
          </div>
        )}

        {isApplicationPending && (
          <div className="space-y-3">
            <div>
              <Label htmlFor="additionalCoins" className="text-sm">
                Add More Coins to Bid
              </Label>
              <div className="flex gap-2 mt-1">
                <Input
                  id="additionalCoins"
                  type="number"
                  min="1"
                  value={additionalCoins}
                  onChange={(e) => setAdditionalCoins(parseInt(e.target.value) || 1)}
                  className="flex-1"
                />
                <Button 
                  onClick={handleIncreaseBid}
                  disabled={increaseBidMutation.isPending}
                  size="sm"
                  className="px-4"
                >
                  {increaseBidMutation.isPending ? "Adding..." : "Add Bid"}
                </Button>
              </div>
              <p className="text-xs text-gray-500 mt-1">
                New total bid: {currentBid + additionalCoins} coins
              </p>
            </div>
          </div>
        )}

        {!isApplicationPending && (
          <div className="text-center py-2">
            <p className="text-xs text-gray-500">
              Bidding is only available for pending applications
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/pages/forgot-password.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Link } from "wouter";
import { ArrowLeft, Mail, CheckCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function ForgotPassword() {
  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [emailSent, setEmailSent] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!email.trim()) {
      toast({
        title: "Error",
        description: "Please enter your email address",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      const response = await fetch("/api/auth/forgot-password", {
        method: "POST",
        body: JSON.stringify({ email }),
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to send password reset email");
      }

      setEmailSent(true);
      toast({
        title: "Reset Link Sent",
        description: "If the email exists, a password reset link has been sent.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to send password reset email",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (emailSent) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <img 
                src="/attached_assets/JIGZ%20LOGO-01_1754969521380.jpg" 
                alt="Jigz Logo" 
                className="h-12 w-auto"
              />
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <CheckCircle className="h-6 w-6 text-green-600" />
            </div>
            <CardTitle>Check Your Email</CardTitle>
            <CardDescription>
              If an account with that email exists, we've sent you a password reset link.
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <p className="text-sm text-gray-600 text-center">
              The reset link will expire in 1 hour for security reasons.
            </p>
            <div className="space-y-2">
              <Link href="/login">
                <Button variant="outline" className="w-full">
                  Back to Login
                </Button>
              </Link>
              <Button
                variant="ghost"
                className="w-full"
                onClick={() => {
                  setEmailSent(false);
                  setEmail("");
                }}
              >
                Send Another Reset Link
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <Card className="w-full max-w-md">
        <CardHeader>
          <div className="flex items-center mb-4">
            <Link href="/login">
              <Button variant="ghost" size="sm" className="p-2">
                <ArrowLeft className="h-4 w-4" />
              </Button>
            </Link>
          </div>
          <div className="text-center">
            <div className="flex justify-center mb-4">
              <img 
                src="/attached_assets/JIGZ%20LOGO-01_1754969521380.jpg" 
                alt="Jigz Logo" 
                className="h-12 w-auto"
              />
            </div>
            <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Mail className="h-6 w-6 text-blue-600" />
            </div>
            <CardTitle>Forgot Password?</CardTitle>
            <CardDescription>
              Enter your email address and we'll send you a link to reset your password.
            </CardDescription>
          </div>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <Input
                id="email"
                type="email"
                placeholder="Enter your email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                autoFocus
                disabled={isLoading}
              />
            </div>

            <Button 
              type="submit" 
              className="w-full" 
              disabled={isLoading}
            >
              {isLoading ? "Sending..." : "Send Reset Link"}
            </Button>

            <div className="text-center">
              <Link href="/login">
                <Button variant="ghost" size="sm">
                  Back to Login
                </Button>
              </Link>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/reset-password.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Link, useLocation } from "wouter";
import { Lock, CheckCircle, AlertCircle, Eye, EyeOff } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function ResetPassword() {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [resetSuccess, setResetSuccess] = useState(false);
  const [token, setToken] = useState("");
  const { toast } = useToast();
  const [location] = useLocation();

  useEffect(() => {
    // Extract token from URL parameters
    const urlParams = new URLSearchParams(location.split('?')[1] || '');
    const tokenParam = urlParams.get('token');
    if (tokenParam) {
      setToken(tokenParam);
    } else {
      toast({
        title: "Invalid Link",
        description: "The password reset link is invalid or missing the token.",
        variant: "destructive",
      });
    }
  }, [location, toast]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!token) {
      toast({
        title: "Error",
        description: "No reset token found. Please request a new password reset link.",
        variant: "destructive",
      });
      return;
    }

    if (password.length < 8) {
      toast({
        title: "Error",
        description: "Password must be at least 8 characters long",
        variant: "destructive",
      });
      return;
    }

    if (password !== confirmPassword) {
      toast({
        title: "Error",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      const response = await fetch("/api/auth/reset-password", {
        method: "POST",
        body: JSON.stringify({ token, password }),
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to reset password");
      }

      setResetSuccess(true);
      toast({
        title: "Password Reset Successful",
        description: "Your password has been reset successfully. You can now sign in.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to reset password",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (resetSuccess) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <img 
                src="/attached_assets/JIGZ%20LOGO-01_1754969521380.jpg" 
                alt="Jigz Logo" 
                className="h-12 w-auto"
              />
            </div>
            <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <CheckCircle className="h-6 w-6 text-green-600" />
            </div>
            <CardTitle>Password Reset Complete</CardTitle>
            <CardDescription>
              Your password has been successfully reset. You can now sign in with your new password.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Link href="/login">
              <Button className="w-full">
                Sign In
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!token) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <img 
                src="/attached_assets/JIGZ%20LOGO-01_1754969521380.jpg" 
                alt="Jigz Logo" 
                className="h-12 w-auto"
              />
            </div>
            <div className="w-12 h-12 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <AlertCircle className="h-6 w-6 text-red-600" />
            </div>
            <CardTitle>Invalid Reset Link</CardTitle>
            <CardDescription>
              This password reset link is invalid or has expired.
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <Link href="/forgot-password">
              <Button className="w-full">
                Request New Reset Link
              </Button>
            </Link>
            <Link href="/login">
              <Button variant="outline" className="w-full">
                Back to Login
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <img 
              src="/attached_assets/JIGZ%20LOGO-01_1754969521380.jpg" 
              alt="Jigz Logo" 
              className="h-12 w-auto"
            />
          </div>
          <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <Lock className="h-6 w-6 text-blue-600" />
          </div>
          <CardTitle>Reset Your Password</CardTitle>
          <CardDescription>
            Enter your new password below. Make sure it's strong and secure.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="password">New Password</Label>
              <div className="relative">
                <Input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter new password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  minLength={8}
                  disabled={isLoading}
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowPassword(!showPassword)}
                  disabled={isLoading}
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </Button>
              </div>
              <p className="text-sm text-gray-500">
                Password must be at least 8 characters long
              </p>
            </div>

            <div className="space-y-2">
              <Label htmlFor="confirmPassword">Confirm New Password</Label>
              <div className="relative">
                <Input
                  id="confirmPassword"
                  type={showConfirmPassword ? "text" : "password"}
                  placeholder="Confirm new password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                  disabled={isLoading}
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  disabled={isLoading}
                >
                  {showConfirmPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </Button>
              </div>
            </div>

            <Button 
              type="submit" 
              className="w-full" 
              disabled={isLoading || !password || !confirmPassword}
            >
              {isLoading ? "Resetting..." : "Reset Password"}
            </Button>

            <div className="text-center">
              <Link href="/login">
                <Button variant="ghost" size="sm">
                  Back to Login
                </Button>
              </Link>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/skill-endorsement-.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Coins } from "lucide-react";
import { CategorySearch } from "@/components/category-search";

interface SkillEndorsementModalProps {
  isOpen: boolean;
  onClose: () => void;
  endorseeId: string;
  endorseeName: string;
  jobId: string;
  jobTitle: string;
}

export function SkillEndorsementModal({
  isOpen,
  onClose,
  endorseeId,
  endorseeName,
  jobId,
  jobTitle,
}: SkillEndorsementModalProps) {
  const [skill, setSkill] = useState("");
  const [message, setMessage] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createEndorsementMutation = useMutation({
    mutationFn: async (data: { endorseeId: string; jobId: string; skill: string; message?: string }) => {
      const response = await apiRequest("POST", "/api/skill-endorsements", data);
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create endorsement");
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Skill Endorsed!",
        description: `You have successfully endorsed ${endorseeName} for their ${skill} skills.`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
      queryClient.invalidateQueries({ queryKey: [`/api/users/${endorseeId}/skill-endorsements`] });
      queryClient.invalidateQueries({ queryKey: ["/api/endorsement-statuses"] });
      queryClient.invalidateQueries({ queryKey: [`/api/jobs/${jobId}/has-endorsed/${endorseeId}`] });
      onClose();
      setSkill("");
      setMessage("");
    },
    onError: (error: any) => {
      toast({
        title: "Endorsement Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = () => {
    if (!skill.trim()) {
      toast({
        title: "Skill Required",
        description: "Please specify the skill you want to endorse.",
        variant: "destructive",
      });
      return;
    }

    createEndorsementMutation.mutate({
      endorseeId,
      jobId,
      skill: skill.trim(),
      message: message.trim() || undefined,
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Endorse {endorseeName}</DialogTitle>
          <DialogDescription>
            Endorse {endorseeName} for their work on "{jobTitle}". This costs 5 coins.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="skill">Skill Category to Endorse *</Label>
            <CategorySearch
              value={skill}
              onValueChange={setSkill}
              placeholder="Select or search for a skill category..."
              includeAllCategories={false}
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="message">Optional Message</Label>
            <Textarea
              id="message"
              placeholder="Add a brief message about their work quality..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              maxLength={200}
              rows={3}
            />
          </div>

          <div className="flex items-center gap-2 p-3 bg-blue-50 dark:bg-blue-950 rounded-lg">
            <Coins className="h-4 w-4 text-blue-600 dark:text-blue-400" />
            <span className="text-sm text-blue-700 dark:text-blue-300">
              This endorsement will cost 5 coins
            </span>
          </div>
        </div>

        <DialogFooter>
          <Button
            variant="outline"
            onClick={onClose}
            disabled={createEndorsementMutation.isPending}
          >
            Cancel
          </Button>
          <Button
            onClick={handleSubmit}
            disabled={createEndorsementMutation.isPending || !skill.trim()}
            className="bg-blue-600 hover:bg-blue-700 dark:bg-blue-600 dark:hover:bg-blue-700"
          >
            {createEndorsementMutation.isPending ? (
              <div className="flex items-center gap-2">
                <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full" />
                Endorsing...
              </div>
            ) : (
              <div className="flex items-center gap-2">
                <Coins className="h-4 w-4" />
                Endorse for 5 Coins
              </div>
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/top-bidders-sectio.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Coins, Trophy, Medal, Award, CheckCircle } from "lucide-react";

interface TopBidder {
  name: string;
  coinsBid: number;
  isEmailVerified?: boolean;
}

interface TopBiddersSectionProps {
  jobId: string;
}

export function TopBiddersSection({ jobId }: TopBiddersSectionProps) {
  const { data: topBidders = [], isLoading } = useQuery<TopBidder[]>({
    queryKey: ['/api/jobs', jobId, 'top-bidders'],
    enabled: !!jobId,
  });

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Trophy className="h-5 w-5 mr-2 text-yellow-600" />
            Top Priority Bidders
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center text-gray-500 py-4">Loading...</div>
        </CardContent>
      </Card>
    );
  }

  if (topBidders.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Trophy className="h-5 w-5 mr-2 text-yellow-600" />
            Priority Bidding
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center text-gray-500 py-4">
            <Coins className="h-8 w-8 mx-auto mb-2 text-gray-400" />
            <p>No priority bids yet</p>
            <p className="text-sm">Be the first to bid coins for higher ranking!</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const getRankIcon = (index: number) => {
    switch (index) {
      case 0:
        return <Trophy className="h-4 w-4 text-yellow-500" />;
      case 1:
        return <Medal className="h-4 w-4 text-gray-400" />;
      case 2:
        return <Award className="h-4 w-4 text-amber-600" />;
      default:
        return <div className="w-4 h-4 rounded-full bg-gray-300 text-xs flex items-center justify-center text-white font-bold">{index + 1}</div>;
    }
  };

  const getRankColor = (index: number) => {
    switch (index) {
      case 0:
        return "bg-yellow-50 border-yellow-200";
      case 1:
        return "bg-gray-50 border-gray-200";
      case 2:
        return "bg-amber-50 border-amber-200";
      default:
        return "bg-blue-50 border-blue-200";
    }
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <Trophy className="h-5 w-5 mr-2 text-yellow-600" />
          Top Priority Bidders
        </CardTitle>
        <p className="text-sm text-gray-600 mt-1">
          Applicants who bid coins for higher ranking
        </p>
      </CardHeader>
      <CardContent>
        <div className="space-y-3">
          {topBidders.slice(0, 5).map((bidder, index) => (
            <div 
              key={index} 
              className={`flex items-center justify-between p-3 rounded-lg border ${getRankColor(index)}`}
            >
              <div className="flex items-center space-x-3">
                {getRankIcon(index)}
                <div>
                  <div className="flex items-center gap-1">
                    <span className="font-medium text-gray-900">{bidder.name}</span>
                    {bidder.isEmailVerified && (
                      <CheckCircle className="h-3 w-3 text-green-600" />
                    )}
                  </div>
                  <div className="text-xs text-gray-600">
                    Rank #{index + 1}
                  </div>
                </div>
              </div>
              <Badge variant="secondary" className="flex items-center gap-1">
                <Coins className="h-3 w-3 text-yellow-500" />
                {bidder.coinsBid} coin{bidder.coinsBid !== 1 ? 's' : ''}
              </Badge>
            </div>
          ))}
        </div>

        {topBidders.length > 0 && (
          <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg">
            <p className="text-xs text-blue-800">
              <Coins className="h-3 w-3 inline mr-1" />
              Higher coin bids appear first in the applicant list for better visibility
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

